mapper.select:

  select a.id,a.name,b.typename,a.personnel,a.price 
     from room a, roomtype b 
     where a.type=b.id 
     and a.personnel >= #{param3} 
     and a.id not in (select room_id 
     from book 
     where (checkin >= #{param1} 
     and checkin <#{param2}) 
     or (checkout >#{param1} 
     and checkout <=#{param2}))
